import React from "react";

const Hero = () => {
  return (
    <header className="bg-yellow-500 flex justify-center items-center text-white text-center pt-32 pb-20 m-5 rounded-xl">
        <div className="w-[60%] flex flex-col justify-center items-center space-y-6">

      <h1 className="text-7xl font-bold">Free AI Powered Quiz Generator</h1>
      <p className="text-lg mt-2 w-2/3 text-center">
      OpExams Quiz Generator is an AI-powered tool that creates quizzes based on the text you enter or based on a topic you choose. You can choose multiple choice questions, true or false, open questions, and more.. You can share the quiz with other people.
      </p>
      <p className="text-8xl font-bold mt-4 text-red-900">
        +10,000,000
      </p>
      <p className="text-lg text-red-900 mt-4">
        Generated questions last month.
      </p>
        </div>
    </header>
  );
};

export default Hero;
