import React from "react";

const Hero = () => {
  return (
    <header className="bg-orange-500 text-white text-center py-10 m-5 rounded-xl">
        <div>

      <h1 className="text-6xl font-bold">Free AI Powered Quiz Generator</h1>
      <p className="text-lg mt-2">
        Create quizzes from text or topics in seconds. Choose multiple-choice,
        true/false, or open-ended questions!
      </p>
      <p className="text-xl font-semibold mt-4">
        +10,000,000 Generated questions last month.
      </p>
        </div>
    </header>
  );
};

export default Hero;
